SELECT MAX(id) AS id, MAX(type) AS type, MAX(material_code) AS material_code, MAX(source) AS source, MAX(inbound_id) AS inbound_id, MAX(outbound_id) AS outbound_id, MAX(warehouse_id) AS warehouse_id, MAX(stock_location_id) AS stock_location_id, MAX(rf_id) AS rf_id, MAX(operator) AS operator, MAX(status) AS status, MAX(note) AS note, MAX(del_flag) AS del_flag, MAX(create_time) AS create_time, MAX(update_time) AS update_time, COUNT(*) AS quantity FROM wms_material_transaction
del_flag IS NULL AND inbound_id IS NOT NULL
AND type = #{type}
AND rf_id = #{rfid}
AND inbound_id = #{inboundId}
GROUP BY inbound_id

mapper.update:
UPDATE wms_material_transaction
SET outbound_id = #{outboundId}
WHERE id IN (
SELECT id
FROM (
SELECT id
FROM wms_material_transaction
WHERE 1 = 1

    AND type = #{type}


    AND source = #{source}


    AND status = #{status}


    AND rf_id = #{rfid}


    AND stock_location_id = #{storageLocationId}

AND del_flag IS NULL
AND outbound_id IS NULL
AND inbound_id IS NOT NULL
ORDER BY id ASC
LIMIT #{quantity}
) AS subquery
)

UPDATE wms_material_transaction
SET outbound_id = #{outboundId}
WHERE id IN (
SELECT id
FROM (
SELECT id
FROM wms_material_transaction
WHERE 1 = 1

    AND type = #{type}


    AND source = #{source}


    AND status = #{status}


    AND material_code = #{materialCode}


    AND stock_location_id = #{storageLocationId}

AND del_flag IS NULL
AND outbound_id IS NULL
AND inbound_id IS NOT NULL
ORDER BY id ASC
LIMIT #{quantity}
) AS subquery
)

